-- MILITARY

-- ------------------------------------
-- ------------------------------------
--  Sword of Gideon
-- ------------------------------------
-- ------------------------------------

	SELECT 'SDG_BELIEF_CLASS_MILITARY_' || (SELECT cast(COUNT(BeliefType) + 100 as text) FROM Beliefs WHERE BeliefClassType LIKE 'SDG_BELIEF_CLASS_%'), 'LOC_SDG_BELIEF_CLASS_MILITARY_NAME', 999, (SELECT COUNT(BeliefType) + 100 FROM Beliefs WHERE BeliefClassType LIKE 'SDG_BELIEF_CLASS_%') ;
	SELECT 'SDG_SWORD_OF_GIDEON', 'LOC_SDG_SWORD_OF_GIDEON', 'LOC_SDG_SWORD_OF_GIDEON_DESCRIPTION', 'SDG_BELIEF_CLASS_MILITARY_' || (SELECT cast(COUNT(BeliefType) + 100 as text) FROM Beliefs WHERE BeliefClassType LIKE 'SDG_BELIEF_CLASS_%') ;



-- ------------------------------------
-- ------------------------------------
--  Joshua's Trumpet
-- ------------------------------------
-- ------------------------------------

	SELECT DISTINCT 'SDG_ABILITY_JOSHUAS_TRUMPET', 'SDG_MOD_' || SUBSTR(PromotionClass, 17) || '_CAN_WALL_ATTACK' FROM Units WHERE FormationClass = 'FORMATION_CLASS_LAND_COMBAT' AND Combat > 0 AND RangedCombat = 0 AND Bombard = 0 AND (PromotionClass LIKE '%ANTI_CAVALRY%' OR PromotionClass NOT LIKE '%CAVALRY%') ;
	SELECT DISTINCT 'SDG_MOD_' || SUBSTR(PromotionClass, 17) || '_CAN_WALL_ATTACK', 'DB_DM_CAN_WALL_ATTACK_PROMOTION_CLASS' FROM Units WHERE FormationClass = 'FORMATION_CLASS_LAND_COMBAT' AND Combat > 0 AND RangedCombat = 0 AND Bombard = 0 AND (PromotionClass LIKE '%ANTI_CAVALRY%' OR PromotionClass NOT LIKE '%CAVALRY%') ;
	SELECT DISTINCT 'SDG_MOD_' || SUBSTR(PromotionClass, 17) || '_CAN_WALL_ATTACK', 'PromotionClass', PromotionClass FROM Units WHERE FormationClass = 'FORMATION_CLASS_LAND_COMBAT' AND Combat > 0 AND RangedCombat = 0 AND Bombard = 0 AND (PromotionClass LIKE '%ANTI_CAVALRY%' OR PromotionClass NOT LIKE '%CAVALRY%') ;

	SELECT DISTINCT 'SDG_ABILITY_JOSHUAS_TRUMPET', 'SDG_MOD_' || SUBSTR(PromotionClass, 17) || '_CAN_BYPASS_WALLS' FROM Units WHERE FormationClass = 'FORMATION_CLASS_LAND_COMBAT' AND Combat > 0 AND RangedCombat = 0 AND Bombard = 0 AND (PromotionClass LIKE '%ANTI_CAVALRY%' OR PromotionClass NOT LIKE '%CAVALRY%') ;
	SELECT DISTINCT 'SDG_MOD_' || SUBSTR(PromotionClass, 17) || '_CAN_BYPASS_WALLS', 'DB_DM_CAN_BYPASS_WALLS_PROMOTION_CLASS' FROM Units WHERE FormationClass = 'FORMATION_CLASS_LAND_COMBAT' AND Combat > 0 AND RangedCombat = 0 AND Bombard = 0 AND (PromotionClass LIKE '%ANTI_CAVALRY%' OR PromotionClass NOT LIKE '%CAVALRY%') ;
	SELECT DISTINCT 'SDG_MOD_' || SUBSTR(PromotionClass, 17) || '_CAN_BYPASS_WALLS', 'PromotionClass', PromotionClass FROM Units WHERE FormationClass = 'FORMATION_CLASS_LAND_COMBAT' AND Combat > 0 AND RangedCombat = 0 AND Bombard = 0 AND (PromotionClass LIKE '%ANTI_CAVALRY%' OR PromotionClass NOT LIKE '%CAVALRY%') ;

