	SELECT 'SDG_JOSHUAS_TRUMPET', 'LOC_SDG_JOSHUAS_TRUMPET', 'LOC_SDG_JOSHUAS_TRUMPET_DESCRIPTION', 'SDG_BELIEF_CLASS_MILITARY_' || (SELECT cast(COUNT(BeliefType) + 100 as text) FROM Beliefs WHERE BeliefClassType LIKE 'SDG_BELIEF_CLASS_%') ;



